import React from "react";

class Form extends React.Component {
  constructor() {
    super() 

    this.state = { 
      testState: '',
     }
  }

  handleChange(event) {
    console.log(event.target.value)
  }

  render() {
    return(
      <label>
        <form>
          <select>
            <option value="teste">teste</option>
          </select>
          <textarea value={this.state.testState} onChange={this.handleChange} />
          <input type ="checkbox"></input>
        </form>
      </label>
    ) 
  }
}

export default Form;
