import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super()

    this.state = {
      pokemons: [],
    };
  }

  componentDidMount() {
    this.fetchPokemon();
  }

  fetchPokemon() {
    const URL = 'https://pokeapi.co/api/v2/pokemon';
    fetch(URL)
      .then((response) => response.json())
      .then((data) =>{
        this.setState({
          pokemons: data.results,
        })
      })
  }

  render() {
    return (
