import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super()

    this.state = {
      pokemons: [],
    };
  }

  async fetchPokemon() {
    const URL = 'https://pokeapi.co/api/v2/pokemon';
    const data = await fetch(URL);
    const result = await data.json();
    this.setState({
      pokemons: result,
    });
  }

  render() {
    return (
      <div>
        teste
        {this.fetchPokemon()}
      </div>

